mapper.select:
SELECT od.name, SUM(od.number) AS number FROM order_detail od LEFT JOIN orders o ON od.order_id = o.id WHERE o.status = 5
AND o.order_time >= #{begin}
AND o.order_time <= #{end}
GROUP BY od.name ORDER BY number DESC LIMIT 10

select count(id) from orders


        and order_time >= #{begin}


        and order_time <= #{end}


        and status = #{status}

select sum(amount) from orders


        and order_time >= #{begin}


        and order_time <= #{end}


        and status = #{status}
