mapper.select:
select s.*,c.name categoryName from setmeal s left join category c on s.category_id = c.id
and s.name like concat('%',#{dto.name},'%')
and s.status = #{dto.status}
and s.category_id = #{dto.categoryId}
order by s.create_time desc

select * from setmeal


        and name like concat('%',#{name},'%')


        and category_id = #{categoryId}


        and status = #{status}

mapper.update:
update setmeal
category_id = #{categoryId}, name = #{name}, price = #{price}, status = #{status}, description = #{description}, image = #{image}, update_time = #{updateTime}, update_user = #{updateUser}
where id = #{id}

mapper.select:
select count(id) from setmeal


        and status = #{status}


        and category_id = #{categoryId}
